import { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../contexts/auth';
import { Route, Redirect } from 'react-router-dom';

import './signup.css';

function SignUp() {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rm, setRm] = useState('');
  const [cargo, setCargo] = useState('');

  const { signUp } = useContext(AuthContext);

  function handleSubmit(e){
    e.preventDefault();
    
    if(nome !== '' && email !== '' && password !== ''){
      signUp(email, password, nome, cargo, rm);
      
    }

  }

  function selectCargo (cargo){
    let select = document.getElementById('cargo');
    let option = select.options[select.selectedIndex];
    cargo = document.getElementById('value').value = option.value;
  }

  return (
    <div className="container-center">
      <div className="login">
        <div className="logo-area">
          <h1>SIGOS</h1>
        </div>

        <form onSubmit={handleSubmit}>
          <h2>Cadastrar uma conta</h2>
          <input type="text" placeholder="Seu nome" value={nome} onChange={(e) => setNome(e.target.value)} />
          <input type='text' placeholder='RM' value={rm} onChange={(e) => setRm(e.target.value)}/>
          <input type="text" placeholder="email@email.com" value={email} onChange={ (e) => setEmail(e.target.value) }/>

          <select id='cargo' onChange={selectCargo} value='funcionario'>
              <option disabled selected>- Selecione seu cargo -</option>
                <option value='tecnico' >Técnico</option>
                <option value='professor' >Professor(a)</option>
                <option value='diretor' >Diretor(a)</option>
                <option value='secretario' >Secretario(a)</option>
            </select>
          <input type="password" placeholder="*******" value={password} onChange={(e) => setPassword(e.target.value) } />
          <button type="submit">{loadinAuth ? 'Carregando...' : 'Acessar'}</button>
        </form>  

        <Link to="/">Já tem uma conta? Entre</Link>
      </div>
    </div>
  );
}

export default SignUp;
