import { useState, useEffect, createContext } from 'react';
import firebase from '../services/firebaseConnection';

//Cirando uma constante para ultilizar o creatContext
export const AuthContext = createContext({});

function AuthProvider({children}){
  const[user, setUser] = useState(null);
  const[loadingAuth, setLoadingAuth] = useState(false);
  const[loading, setLoading] = useState(true);

 //Salvando as insformações do locarStrage na constante storegeUser quando a págiana é aberta.
 useEffect(() =>{
  function loadStorage(){
    const storageUser = localStorage.getItem('SistemaUser'); 

    //Transformando o conteudo da constante em objeto novamente e setando o valor da mesma na constante user.
    if(storageUser){
      setUser(JSON.parse(storageUser));
    }
 
    setLoading(false);
  }

  loadStorage();

 }, [])

 async function signup (emai, password, nome, cargo, rm){
   setLoadingAuth(true);
   await firebase.auth().createUserWithEmailAndPassword(email, password)
   .then( async (value) => {
     let uid = value.user.id;
     await firebase.firestore().collection('user')
     .doc(uid).set({
      nome: nome,
      matricula: rm,
      cargo: cargo,
      avatarUrl: null,
     })
     .then( () =>{
       let data = {
        uid: uid,
        emai: value.user.email,
        nome: nome,
        matricula: rm,
        cargo: cargo,
        avatarUrl: null,
       }
     })
     
   })
 }
  
  return(
    <AuthContext.Provider value={{signed: !!user, user, loading}}>
      {children}
    </AuthContext.Provider>
  )
}

export default AuthProvider;
