import './Profile.css';
import { useContext, useState} from 'react';
import '../../components/Header';
import Header from '../../components/Header';
import Title from '../../components/Title';
import {FiSettings, FiUpload} from 'react-icons/fi';
import { AuthContext } from '../../contexts/auth';
import avatar from '../../assets/avatar.png'

export default function Profile(){
  const {user} = useContext(AuthContext);

  const [nome, setNome] = useState(user && user.nome);
  const [email, setEmail] = useState(user && user.email);
  const [rm, setRm] = useState(user && user.rm);
  const [cargo, setCargo] = useState(user && user.cargo);
  const [avatarUrl, setAvatarUrl] = useState(user && user.avatarUrl);
  
  return(
    <div>
      <Header/>
        <div className='content'>
          <Title name='Meu perfil'>
          <FiSettings size={25}/>
          </Title>
          <div className='container'>
            <form className='form-profile'>
              <label className='label-avatar'>
                <span>
                  <FiUpload size={25} color='#FFF'/>
                </span>
                <input type='file' accept='image/*'/>
                <br/>
                {avatarUrl === null ? 
                <img src={avatar} width='250' height='250' alt='Foto de perfil do usuário'/>
                :<img src={avatar} width='250' height='250' alt='Foto de perfil do usuário'/>}
              </label>
              <label>Nome</label>
              <input type='text' value={nome} onChange={(e) => setNome(e.target.value)}/>
              <label>Cargo</label>
              <input type='text' value={cargo} disabled='false'/>
              <label>RM</label>
              <input type='text' value={rm} onChange={(e) => setRm(e.target.value)}/>
              <label>E-mail</label>
              <input type='text' value={email} disabled='false'/>
              <button type='submit'>Salvar</button>
            </form>
          </div>
      </div>
    </div>
  )
}
