import { useState } from 'react';
import { Link } from 'react-router-dom';

import './signup.css';

function SignUp() {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [nome, setNome] = useState("");
  const [rm, setRm] = useState("");

  function handleSubmit(e){
    e.preventDefault();
    alert('CLICOU')
  }

  return (
    <div className='container-center'>
      <div>
        <div className='login'>
          <div className='logo-area'>
            <h1>SIGOS</h1>
          </div>

          <form onSubmit={handleSubmit}>
            <h2>Criar Conta</h2>
            <input type='text' placeholder='nome completo' value={nome} onChange={(e) => setNome(e.target.value)}/>
            <input type='text' placeholder='RM' value={rm} onChange={(e) => setRm(e.target.value)}/>
            <input type='text' placeholder='nome@etec.sp.gov.br' value={email} onChange={(e) => setEmail(e.target.value) }/>
            <select>
            <option disabled selected>- Selecione seu cargo -</option>
              <option value='tecnico'>Técnico</option>
              <option value='professor'>Professor(a)</option>
              <option value='diretor'>Diretor(a)</option>
              <option value='secretario'>Secretario(a)</option>
            </select>
            <input type='password' placeholder='*******' value={senha} onChange={(e) => setSenha(e.target.value)}/>
            <button type='submit'>Cadastrar</button>
          </form>

          <Link to='/'>Já tem uma conta? Entre aqui</Link>
        </div>
      </div>
    </div>
  );
}
export default SignUp;
