import { useState, createContext, useEffect } from 'react';
import firebase from '../services/firebaseConnection';

//Cirando uma constante para ultilizar o creatContext
export const AuthContext = createContext({});

function AuthProvider({ children }){
  const [user, setUser] = useState(null);
  const [loadingAuth, setLoadingAuth] = useState(false);
  const [loading, setLoading] = useState(true);

  //Salvando as insformações do locarStrage na constante storegeUser quando a págiana é aberta.
  useEffect(()=>{

    function loadStorage(){
      const storageUser = localStorage.getItem('SistemaUser');

      //Transformando o conteudo da constante em objeto novamente e setando o valor da mesma na constante user.
      if(storageUser){
        setUser(JSON.parse(storageUser));
        setLoading(false);
      }
  
      setLoading(false);
    }
    
    loadStorage();

  }, [])

  //Realizando o login de um usuário
  async function signIn(email, password){
    setLoadingAuth(true);

    await firebase.auth().createUserWithEmailAndPassword(email, password)
    .then(() => {})
    .catch((error)=>{
      console.log(error);
      setLoading(false); 
    })
    
  }

  //Cadatrando um novo usuário e armazenando as informações no banco de dados.
  async function signUp(email, password, nome, cargo, rm){
    setLoadingAuth(true);

    await firebase.auth().createUserWithEmailAndPassword(email, password)
    .then( async (value)=>{
      let uid = value.user.uid;

      await firebase.firestore().collection('users')
      .doc(uid).set({
        nome: nome,
        rm: rm,
        cargo: cargo,
        avatarUrl: null,
      })
      .then( () => {

        let data = {
          uid: uid,
          nome: nome,
          matricula: rm,
          cargo: cargo,
          email: value.user.email,
          avatarUrl: null
        };

        setUser(data);
        storageUser(data);
        setLoadingAuth(true);

      })

    })
    //Caso a ciração do usuário não seja bem sucedida, mostrar o erro no console.
    .catch((error)=>{
      console.log(error);
      setLoadingAuth(false);
    })

  }

  function storageUser(data){
    localStorage.setItem('SistemaUser', JSON.stringify(data));
  }

//Funçãp para realizar logout
  async function signOut(){
    await firebase.auth().signOut();

    //removendo informações do local storage e da const user.
    localStorage.removeItem('SistemaUser');
    setUser(null);
  }


  return(
    <AuthContext.Provider 
    value={{ 
      signed: !!user,  
      user, 
      loading, 
      signUp,
      signOut
    }}
    >
      {children}
    </AuthContext.Provider>
  )
}

export default AuthProvider;
